    public class HP
    {
        public static double oneHealthInWei  { get; private set; } = Math.Pow(10, 17);
        
        public static async UniTask SetHealthValues(BigInteger maxHealthWeiInMainCoin)
        {
            switch(AvailableNetworks.CurrentNetworkData.chain)
            {
                case "near":
                        
                    Debug.Log($"#debug #motodexcall #contracts");
                     oneHealthInWei = double.Parse(await ContractHandler.MotodDexCall("valueInMainCoinCorrect", Abis.MotoDEXnftABI, Chain.InUse, Network.InUse, Contracts.MotoDEXnft, "7"))/5;
                     oneHealthInWei /= Const.NearHealth.nearHpDiff;
                break;
                case "concordium":
                    
                Debug.Log($"#debug #motodexcall #contracts");
                    oneHealthInWei = double.Parse(await ContractHandler.MotodDexCall("getPriceForType", Abis.MotoDEXnftABI, Chain.InUse, Network.InUse, Contracts.MotoDEXnft, "7"))/5;
                    oneHealthInWei *= Const.ConcordiumHealth.concordiumHpDiff;
                break;
                default:
                    // double priceForType = double.Parse(await ContractHandler.MotodDexCall("getPriceForType", Abis.MotoDEXnftABI, Chain.InUse, Network.InUse, Contracts.MotoDEXnft, nftType));

                    oneHealthInWei = (double)maxHealthWeiInMainCoin/20;

                    Debug.Log($"#debug #health SetHealthValues maxHealthWeiInMainCoin {maxHealthWeiInMainCoin}, oneHealthInWei = {oneHealthInWei}");

                break;
            }
            
            Debug.Log("#debug #health SetHealthValues OneHealthInWei = " + oneHealthInWei.ToString("0"));
        }
    }
    
    public class MinimalFee
    {
        public static double bidMinimalFee  { get; private set; } = 0.001;
        
        public static async UniTask SetBidMinimalFee()
        {
            if (AvailableNetworks.CurrentNetworkData.chain == "near")
            {
                Debug.Log($"#debug #motodexcallWithoutSerialize #contracts");
                bidMinimalFee = double.Parse(await ContractHandler.MotodDexCallWithoutSerialize("getMinimalFeeRate",
                    Abis.MotoDEXABI, Chain.InUse, Network.InUse, Contracts.MotoDEX, "[]"));
            }
            else
            {
                Debug.Log($"#debug #motodexcallWithoutSerialize #contracts");
                bidMinimalFee = double.Parse(await ContractHandler.MotodDexCallWithoutSerialize("minimalFeeInUSD",
                    Abis.MotoDEXABI, Chain.InUse, Network.InUse, Contracts.MotoDEX, "[]"));
                Debug.Log($"#debug #bidMinimalFee - {bidMinimalFee}");
            }

            if (AvailableNetworks.CurrentNetworkData.chain == "near")
                bidMinimalFee = bidMinimalFee / Math.Pow(10, Math.Floor(Math.Log10(bidMinimalFee)) + 1);
            else if (AvailableNetworks.CurrentNetworkData.chain == "concordium" || AvailableNetworks.CurrentNetworkData.chain == "skale")
                bidMinimalFee /= Const.Wallet.CCDToHuman;
            else
                bidMinimalFee = 0.001;
            
            Debug.Log("bidMinimalFee = " + bidMinimalFee.ToString("G17"));
        }
    }

