    public class ConnectChain : MonoBehaviour
    {
        public Action onConnected;
        public Action onConnectedFail;
#if UNITY_WEBGL && !UNITY_EDITOR
        [DllImport("__Internal")]
        private static extern void Web3Connect();

        [DllImport("__Internal")]
        private static extern string ConnectAccount();

        [DllImport("__Internal")]
        private static extern void SetConnectAccount(string value);
        
        [DllImport("__Internal")]
        private static extern void Web3NearConnect(string network);

        [DllImport("__Internal")]
        private static extern string NearConnectAccount();

        [DllImport("__Internal")]
        private static extern void SetNearConnectAccount(string value);
#endif
        
        public string account;

        public bool qrConnect = false;

        private void Awake()
        {
            if ( GameObject.FindGameObjectsWithTag("MenuManager").Length > 1 )
            {
                Destroy(gameObject);
                return;
            }
            DontDestroyOnLoad(gameObject);
        }

        public bool IsConnected() => !string.IsNullOrEmpty(account);

        public void SetConnectCallback(Action onConnected, Action onFail)
        {
            this.onConnected = onConnected;
            this.onConnectedFail = onFail;
        }

        public void SetQrConnect(bool active) // Must be set in the Connect buttons(Metamask & WalletConnect)
                                              // And Login() if WalletConnect button
        {
            qrConnect = active;
        }

        public async void Login()
        {
            Debug.Log("Login to account");
#if (UNITY_ANDROID || UNITY_IOS) && !UNITY_EDITOR
            Debug.Log("Connecting to metamask");
            // Web3Wallet.url = "https://metamask.app.link/dapp/" + MotoDexApi.ChainSafeApi;
#else
            Web3Wallet.url = "https://" + MotoDexApi.ChainSafeApi;
#endif
#if UNITY_WEBGL && !UNITY_EDITOR
            if (AvailableNetworks.CurrentNetworkData.chain == "near")
                Web3NearConnect(AvailableNetworks.CurrentNetworkData.network);
            else
                Web3Connect();
            await OnConnected();
            return;
#elif UNITY_ANDROID
            if (AvailableNetworks.CurrentNetworkData.chain == "near")
            {
                var nearNetwork = AvailableNetworks.CurrentNetworkData.network;
                var nearMainUrl = nearNetwork == "mainnet" ? "motodex://wallet.near.org" : "motodex://testnet.mynearwallet.com";
                await NearPersistentManager.Instance.WalletAccount.RequestSignIn(
                    AvailableNetworks.CurrentNetworkData.contracts[0],
                    "Near Unity Client",
                    new Uri(nearMainUrl + "/success"),
                    new Uri(nearMainUrl + "/fail"),
                    new Uri(nearMainUrl)
                );
                if (!NearPersistentManager.Instance.WalletAccount.IsSignedIn())
                {
                    // string debugUrl = "";
                    // Debug.Log(debugUrl);
                    // await NearCompleteSignIn(debugUrl);
                    Application.deepLinkActivated += async (string url) => await NearCompleteSignIn(url);
                }
                else
                    OnNearMobileConnected();
                return;
            }
#endif
            Debug.Log($"do prefs has account {PlayerPrefs.HasKey(PlayerPrefsKeys.Account)}");
            if (PlayerPrefs.HasKey(PlayerPrefsKeys.Account))
            {
                account = PlayerPrefs.GetString(PlayerPrefsKeys.Account);
                Debug.Log("#debug #account ACCOUNT: " + account);
                onConnected?.Invoke();
                return;
            }

            if (!qrConnect)
            {
                MobileDesktopConnect();
                return;
            }
            var walletConnectData = await WalletConnect.Instance.Connect();
            Debug.Log($"#debug #account Accounts {walletConnectData?.accounts?.Length}");
            account = walletConnectData.accounts[0];
            foreach (var account in walletConnectData.accounts)
            {
                Debug.Log(account);
            }
            
            Debug.Log($"account length {account.Length}");
            if (account.Length == 42)
            {
                // save account
                account = walletConnectData.accounts[0];
                PlayerPrefs.SetString(PlayerPrefsKeys.Account, account);
                Debug.Log($"#debug #account save account");
            }
            onConnected?.Invoke();
//#endif
        }

        private async void MobileDesktopConnect()
        {
            Debug.Log($"I am in MobileDesktopConnect");
            // get current timestamp
            var timestamp = (int)System.DateTime.UtcNow.Subtract(new System.DateTime(1970, 1, 1)).TotalSeconds;
            // set expiration time
            var expirationTime = timestamp + 60;
            // set message
            var message = expirationTime.ToString();
            // sign message
            var signature = await Web3Wallet.Sign(message);
            // verify account
            var account = EVM.SignVerifySignature(signature, message);
            var now = (int)System.DateTime.UtcNow.Subtract(new System.DateTime(1970, 1, 1)).TotalSeconds;
            // validate
            
            Debug.Log($"account full string {account}");
            Debug.Log($"account length {account.Length}");
            if (account.Length == 42 && expirationTime >= now)
            {
                this.account = account;
                PlayerPrefs.SetString(PlayerPrefsKeys.Account, account);
                onConnected?.Invoke();
            }
        }

        public async UniTask<string> ShowBalance(string chain = null, string network = null)
        {
            Debug.Log($"ConnectChain invoked with parameters:" +
                $"\nchain={chain}," +
                $"\nnetwork={network}");
            if ( chain == null ) chain = Chain.InUse;
            if ( network == null ) network = Network.InUse;
#if UNITY_WEBGL && !UNITY_EDITOR
            string balance = await Web3GL.GetBalance();
#else
            string balance = await EVM.BalanceOf(this.account);
#endif
            return balance;
        }

        public async UniTask<int> GetBalanceByContract(Action onErrorClick = null,
            string chain = null,
            string network = null,
            string contract = null)
        {
            if ( chain == null ) chain = Chain.InUse;
            if ( network == null ) network = Network.InUse;
            if ( contract == null ) contract = Contracts.MotoDEXnft;
            
            Debug.Log($"#debug #motodexcall #contracts");
            var balance = await ContractHandler.MotodDexCall("balanceOf", Abis.MotoDEXnftABI, chain, network, contract, account);
            Debug.Log($"balance before Parsing - {balance}");
            if (int.TryParse(balance, out int balanceInt))
            {
                Debug.Log($"#debug #balance - {balance} balanceInt - {balanceInt}");
                return balanceInt;
            }
            else
            {
                 string message = "Please top up your balance and re-enter the game. Wallet address was copied to clipboard. Your wallet is:";
                 Debug.Log($"#debug: GetBalanceByContract,  message: {message}");                
                
                 PopUpManager.Instance.PushPopUp("zero_balnce", "Balance error", message , PlayerPrefs.GetString(PlayerPrefsKeys.Account), false, PopupButton.ok, true, onApprove: onErrorClick);
                return 0;
            }
        }

        public async UniTask<string> GetAllErc721(
            string chain = null,
            string network = null,
            string contract = null)
        {
            if ( chain == null ) chain = Chain.InUse;
            if ( network == null ) network = Network.InUse;
            if ( contract == null ) contract = Contracts.MotoDEXnft;
            string response;
#if UNITY_WEBGL && !UNITY_EDITOR
            if (AvailableNetworks.CurrentNetworkData.chain == "near")
            {
                response = await Web3GL.ListNearNFTs(AvailableNetworks.CurrentNetworkData.network, contract, account);
            }
            else
            {
                Debug.Log($"#debug #call #AllErc721 - {contract}");
                response = await Web3GL.AllErc721(Abis.MotoDEXnftABI, contract);
                Debug.Log($"#debug #response #AllErc721 - {response}");
            }
#elif UNITY_ANDROID
            if (AvailableNetworks.CurrentNetworkData.chain == "near")
                response = await AndroidNear.NftTokensForOwners(account);
            else
                response = await EVM.AllErc721(chain, network, account, contract);
#else
            response = await EVM.AllErc721(chain, network, account, contract);
#endif
            return response;
        }
        
        
        public async UniTask<AddedToken> GetAllTokenIdsAndOwners()
        {
            AddedToken response = await AddedCheck.NFTAddedCheck(account);
            return response;
        }

#if UNITY_WEBGL && !UNITY_EDITOR
        private async UniTask OnConnected()
        {
            SetNearConnectAccount("");
            SetConnectAccount("");
            if (AvailableNetworks.CurrentNetworkData.chain == "near")
                account = NearConnectAccount();
            else
                account = ConnectAccount();
            while (account == "")
            {
                await new WaitForSeconds(1f);
                if (AvailableNetworks.CurrentNetworkData.chain == "near")
                    account = NearConnectAccount();
                else
                    account = ConnectAccount();
            }

            Debug.Log($"#debug #account OnConnected account {account}");

            if (account == "fail")
            {
                Debug.Log("Connection error, account: " + account);
                account = "";
                SetNearConnectAccount("");
                SetConnectAccount("");
                PopUpManager.Instance.PushPopUp(
                    "fail_connect",
                    "Connection error",
                    "OnConnected",
                    "",
                    false, PopupButton.ok, true, null, null);
                onConnectedFail?.Invoke();
                return;
            }

            PlayerPrefs.SetString(PlayerPrefsKeys.Account, account);
            PlayerPrefs.Save();
            SetNearConnectAccount("");
            SetConnectAccount("");
            onConnected?.Invoke();
        }
#endif

#if UNITY_ANDROID

        private void OnNearMobileConnected()
        {
            account = NearPersistentManager.Instance.WalletAccount.GetAccountId();
            PlayerPrefs.SetString(PlayerPrefsKeys.Account, account);
            PlayerPrefs.Save();
            Debug.Log(account);
            onConnected?.Invoke();
        }
        
        private async Task NearCompleteSignIn(string url)
        {        
            await NearPersistentManager.Instance.WalletAccount.CompleteSignIn(url);
            if(NearPersistentManager.Instance.WalletAccount.IsSignedIn() == true)
            {
                OnNearMobileConnected();
            }        
        }
#endif
    }
